public class Phonebook {    String[] names;    int[] numbers;    int next;    int maximum;    Phonebook() {        names = new String[1000];        numbers = new int[1000];        next = 0;        maximum = 1000;    }    Phonebook(int n) {        names = new String[n];        numbers = new int[n];        next = 0;        maximum = n;    }    public void setEntry(String n, int t) {        names[next] = n;        numbers[next] = t;        next = next + 1;    }    public int getNumber(String n) {        int i = 0;        while (i < next) {            if (names[i].equals(n)) {                return numbers[i];            }            i = i + 1;        }        return -1;    }    public int changeNumber(String name, int number) {        for (int i = 0; i < next; i++) {            if (names[i].equals(name)) {                numbers[i] = number;                return 0;            }        }        return -1;    }    public int deleteEntry(String name) {        for (int i = 0; i < next; i++) {            if (names[i].equals(name)) {                System.arraycopy(names, i + 1, names, i, next - i - 1);                System.arraycopy(numbers, i + 1, numbers, i, next - i - 1);                next--;                return 0;            }        }        return -1;    }}