import java.io.*;class TestClock2 {    public static void main(String args[]) throws IOException {        System.out.println("creating a new clock");        Clock2 a = new Clock2();        BufferedReader inKbd = new BufferedReader(new InputStreamReader(System.in));        String inputLine;        System.out.print("Hours ? ");        inputLine = inKbd.readLine();        int h = Integer.parseInt(inputLine);        System.out.print("Minutes ? ");        inputLine = inKbd.readLine();        int m = Integer.parseInt(inputLine);        System.out.print("Seconds ? ");        inputLine = inKbd.readLine();        int s = Integer.parseInt(inputLine);        System.out.print("Day ? ");        inputLine = inKbd.readLine();        int D = Integer.parseInt(inputLine);        System.out.print("Month ? ");        inputLine = inKbd.readLine();        int M = Integer.parseInt(inputLine);        System.out.println("setting a new hour and date");        a.setTime(h, m, s);        a.setDate(D, M);        System.out.println("Time is now: " + a.getTime());        System.out.println("Date is now: " + a.getDay() + "/" + a.getMonth());    }}